import React, { Component } from 'react';
import Layout from './hoc/Layout/Layout';
import StoresContainer from './containers/StoresContainer/StoresContainer';

class App extends Component {
  state = {
    stores: []
  }

  componentDidMount() {
    axios.get('https://course-project-react.firebaseio.com/stores.json')
      .then(response => {
        this.setState({stores: response.data});
      })
      .catch(error => {
        console.log(error);
      })
  }

  render() {
    const stores = this.state.stores.map((store, index) => (
      <Route key = {index} path = {"/" + store.id} render = {() => <Store storeId = {store.id}/>}/>
    ));

    return (
      <div className="App">
        <Layout>
          <Redirect from = "/" to = "/0" />
          {stores}
          <Loader mainColor = '#DA70D6' shadow = 'rgb(255, 210, 253)'/>
        </Layout>
      </div>
    );
  }
}

export default App;
