import React, { Component } from 'react';
import StoreMenuItem from '../StoreMenuItem/StoreMenuItem';
import axios from 'axios';

class StoreMenu extends Component {
    state = {
        stores: null
    }

    componentDidMount() {
        axios.get('https://course-project-react.firebaseio.com/stores.json')
            .then(response => {
                this.setState({stores: response.data});
            })
            .catch(error => {
                console.log(error);
            })
    }

    render() {
        const storeItems = this.state.stores.map((store, index) => (
            <StoreMenuItem key = {index} storeId = {store.id} storeName = {store.title}/>
        ));

        return (
            {storeItems}
        );
    }
}

export default StoreMenu;
