import React, { Component } from 'react';
import {Route, Redirect} from 'react-router-dom';
import Store from './containers/Store/Store';
import axios from 'axios';
import Loader from './UI/Loader/Loader';

class StoresContainer extends Component {
    state = {
        stores: []
      }
    
      componentDidMount() {
        axios.get('https://course-project-react.firebaseio.com/stores.json')
          .then(response => {
            this.setState({stores: response.data});
          })
          .catch(error => {
            console.log(error);
          })
      }
    
      render() {
        const stores = this.state.stores.map((store, index) => (
          <Route key = {index} path = {"/" + store.id} render = {() => <Store storeId = {store.id}/>}/>
        ));
    
        return (
          <div>
            <Layout>
              <Redirect from = "/" to = "/0" />
              {stores}
              <Loader mainColor = '#DA70D6' shadow = 'rgb(255, 210, 253)'/>
            </Layout>
          </div>
        );
      }
}

export default StoresContainer;
